import React, { useEffect, useState } from "react";
import { Form, FormGroup, Label, Input } from "reactstrap";
import axios from "axios";

import "../compStyle.css";
import { AncestorTest, AncestorDetails } from "../../types";
import { db } from "../../services/firebase";

export default function AllAncestorsPage() {
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState<AncestorTest[]>([]);
  const [ancestor, setAncestor] = useState<AncestorTest[]>([]);

  useEffect(() => {
    const ancestors = db
      .collection("ancestors")
      .get()
      .then((snapshot) => {
        const returnedAncestors: AncestorTest[] = [];
        snapshot.forEach((snap) => {
          //@ts-ignore
          returnedAncestors.push(snap.data());
        });
        setAncestor(returnedAncestors);
      });
  }, []);

  useEffect(() => {
    const results = ancestor.filter((ancestor) =>
      ancestor.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setSearchResults(results);
  }, [searchTerm]);

  console.log(ancestor);

  return (
    <div>
      <h2> All Ancestors page</h2>
      <div className="content-body">
        <div className="search-body">
          <Form className="searchbar-form">
            <FormGroup>
              <Input
                type="text"
                placeholder="Search"
                value={searchTerm}
                onChange={(event) => setSearchTerm(event.target.value)}
              />
              <div className="search-results">
                {searchResults.map((ancestor) => (
                  <div>
                    <h2>{ancestor.name}</h2>
                    <p> Year Of Birth:{ancestor.yearOfBirth} </p>
                    <p>Job: {ancestor.job}</p>
                    <p>
                      Children:{ancestor.children ? ancestor.children : "no"}
                    </p>
                    <p>Married: {ancestor.married ? "yes" : "no"}</p>
                    <p>Deceased: {ancestor.deceased ? "yes" : "no"}</p>
                  </div>
                ))}
              </div>
            </FormGroup>
          </Form>
        </div>
      </div>
    </div>
  );
}
